import React, { useContext } from 'react';
import { SearchContext } from '../../shared/context/search-context';

import './searchbar.css';

const tooltipText = `1. User sees an input
  2. Mouse hovers the input
  3. User clicks the input
  4. User starts typing
  5. Selection made
`;

const SearchBar = ({ type = 'text', label, placeholder, ...otherProps }) => {
  const search = useContext(SearchContext);
  return (
    <div>
      <span data-tooltip={tooltipText}>Hover me for more information</span>
      <input
        className='search-input'
        type={type}
        label={label}
        onChange={search.handleChange}
        onClick={search.handleInputClick}
        placeholder={placeholder}
        {...otherProps}
      />

      <img
        className='search-icon'
        src='data:image/svg+xml;utf8;base64,'
        alt='search icon'
      />

      <div className='arrow down'></div>

      <label htmlFor='Contact' className='search-input-label'>
        {label}{' '}
      </label>
    </div>
  );
};

export default SearchBar;
